var apps_search_found = false;
var app_index = global('APP_INDEX')
;
	var keyboard_root_path = global('KEYBOARD_ROOT_PATH');
for(var i = 1; i <= app_index; i++) {
	var app_pinyin = global('APP_PINYINS' + i);
	if(app_pinyin.indexOf(keyboard) == -1) {
		continue;
	}

	apps_search_found = true;
	var app_name = global('APP_NAMES' + i);
	var app_package = global('APP_PACKAGES' + i);
	apps_html += "<div class='app' onClick=\"exec(this, '" + app_package + "');\"><img src='" + keyboard_root_path + "/icon/"  + app_package + ".png'> " + app_name + "</div>";
}
if (!apps_search_found) {
	apps_html += "<div class='app'> 未找到应用</div>";
}
